import{_ as e,o as i,c as a,a as r}from"./app.afe75d95.js";var t="/TIL/assets/Tier.001.8fa08991.jpeg",c="/TIL/assets/Tier.002.4c6f430f.jpeg",o="/TIL/assets/Tier.003.f557416a.jpeg",d="/TIL/assets/Tier.004.6cde6101.jpeg";const p={},n=r('<h1 id="multi-tier-architecture" tabindex="-1"><a class="header-anchor" href="#multi-tier-architecture" aria-hidden="true">#</a> Multi-Tier Architecture</h1><hr><blockquote><p>\uD50C\uB7AB\uD3FC\uC744 \uB2E4\uC218 \uACC4\uCE35\uC73C\uB85C \uB098\uB204\uC5B4 \uBCC4\uB3C4\uC758 \uBB3C\uB9AC\uC801(Physical) / \uB17C\uB9AC\uC801(Logical) \uC7A5\uCE58\uC5D0 \uAD6C\uCD95, \uC6B4\uC601\uD558\uB294 \uD615\uD0DC</p></blockquote><p>\uC77C\uBC18\uC801\uC73C\uB85C 3-Tier (Presentation / Application / Data) \uD615\uD0DC\uB85C \uC6B4\uC6A9\uB418\uB294 \uACBD\uC6B0\uAC00 \uB9CE\uC73C\uBA70, \uC0C1\uD669\uC5D0 \uB530\uB77C \uB354 \uB9CE\uC740 \uACC4\uCE35\uC744 \uB450\uAC70\uB098 \uBC18\uB300\uC758 \uACBD\uC6B0\uB3C4 \uC874\uC7AC\uD568</p><h2 id="presentation-tier" tabindex="-1"><a class="header-anchor" href="#presentation-tier" aria-hidden="true">#</a> Presentation Tier</h2><p>User Interface (UI)\uB97C \uC9C0\uC6D0 - <code>Front-end</code>, \uC0AC\uC6A9\uC790\uC640 \uC9C1\uC811 \uB9C8\uC8FC\uD558\uB294 \uACC4\uCE35</p><p>e.g. <code>HTML</code>, <code>Javascript</code>, <code>CSS</code>, Image Assets, etc,.</p><h2 id="application-tier" tabindex="-1"><a class="header-anchor" href="#application-tier" aria-hidden="true">#</a> Application Tier</h2><p>\uD2B9\uC815 \uADDC\uCE59\uC73C\uB85C \uBC14\uD0D5\uC73C\uB85C \uD558\uB294 \uC694\uCCAD \uC815\uBCF4\uC758 \uAC00\uACF5 --&gt; \uB3D9\uC801 \uB370\uC774\uD130 \uC81C\uACF5</p><p>== Business Logic / Transaction Tier</p><p><code>Client Tier</code> \uCE21\uBA74\uC5D0\uC11C\uC758 Application Tier\uB294 \uC11C\uBC84\uC640 \uAC19\uC774 \uB3D9\uC791 (Response), <code>Data Tier</code> \uCE21\uBA74\uC5D0\uC11C\uC758 Application Tier\uB294 \uD074\uB77C\uC774\uC5B8\uD2B8\uC640 \uAC19\uC774 \uB3D9\uC791 (Request)</p><p>--&gt; <code>Middleware</code> or <code>Back-end</code></p><p>e.g. <code>PHP</code>, <code>Java</code></p><h2 id="data-tier" tabindex="-1"><a class="header-anchor" href="#data-tier" aria-hidden="true">#</a> Data Tier</h2><p>Database\uC5D0 \uC811\uADFC\uC5D0 \uB370\uC774\uD130\uB97C \uC77D\uACE0 \uC4F0\uB294 \uAC83\uC744 \uAD00\uB9AC\uD568</p><p>Data Tier \uB610\uD55C Back-end\uB85C \uBCFC \uC218 \uC788\uC74C</p><p>e.g. <code>DBMS</code> --&gt; <code>MySQL</code>, <code>MongoDB</code>, etc,.</p><h2 id="_1-tier" tabindex="-1"><a class="header-anchor" href="#_1-tier" aria-hidden="true">#</a> 1-Tier</h2><p><img src="'+t+'" alt="1-Tier"></p><p>\uD558\uB098\uC758 \uBB3C\uB9AC\uC801 \uC11C\uBC84 (\uCEF4\uD4E8\uD130)\uC5D0 3\uAC00\uC9C0 \uB2E4\uB978 \uAE30\uB2A5\uC744 \uD568\uAED8 \uAD6C\uD604, \uBB3C\uB9AC\uC801 \uC7A5\uBE44 \uBCC0\uACBD \uC2DC \uBAA8\uB4E0 \uAD6C\uC131\uC758 \uBCC0\uACBD \uD544\uC694</p><h2 id="_2-tier" tabindex="-1"><a class="header-anchor" href="#_2-tier" aria-hidden="true">#</a> 2-Tier</h2><p><img src="'+c+'" alt="2-Tier"></p><p><code>Client Tier</code>\uC640 <code>Data Tier</code>\uC758 \uBB3C\uB9AC\uC801 \uAD6C\uBD84 --&gt; \uAC01 \uACC4\uCE35\uC5D0\uC11C\uC758 \uBCC0\uACBD\uC740 \uC11C\uB85C\uC5D0 \uC601\uD5A5\uC744 \uBC1B\uC9C0 \uC54A\uC74C</p><p>\uD074\uB77C\uC774\uC5B8\uD2B8\uC5D0 \uBAA8\uB4E0 \uB85C\uC9C1\uC774 \uC874\uC7AC\uD558\uAE30 \uB54C\uBB38\uC5D0 <code>\uC7AC\uC0AC\uC6A9\uC131</code> \uCE21\uBA74\uC5D0\uC11C <em>\uBC14\uB78C\uC9C1\uD558\uC9C0 \uBABB\uD568</em>, <code>\uC790\uC6D0 \uD65C\uC6A9</code> \uCE21\uBA74\uC5D0\uC11C\uB3C4 _\uBE44\uD6A8\uC728\uC801_\uC774\uBA70 <code>\uBAA8\uB2C8\uD130\uB9C1 \uBC0F \uAD00\uB9AC</code>\uAC00 <em>\uC6A9\uC774\uD558\uC9C0 \uBABB\uD568</em></p><h2 id="_3-tier-1" tabindex="-1"><a class="header-anchor" href="#_3-tier-1" aria-hidden="true">#</a> 3-Tier (1)</h2><p><img src="'+o+'" alt="3-Tier"></p><p>\uAC01 \uACC4\uCE35 \uBAA8\uB450 \uBB3C\uB9AC\uC801\uC778 \uAD6C\uBD84\uC73C\uB85C \uAD6C\uC131 --&gt; \uAC01 \uACC4\uCE35\uC5D0\uC11C\uC758 \uBCC0\uD654\uB294 \uC11C\uB85C \uC601\uD5A5 \uC5C6\uC774 \uB3C5\uB9BD\uC801\uC73C\uB85C \uC6B4\uC601</p><h2 id="_3-tier-2" tabindex="-1"><a class="header-anchor" href="#_3-tier-2" aria-hidden="true">#</a> 3-Tier (2)</h2><p><img src="'+d+'" alt="3-Tier (2)"></p><p><code>Development</code> / <code>Staging</code> / <code>Production</code> \uD615\uD0DC\uB85C, <code>Development</code> \uB2E8\uACC4\uC5D0\uC11C\uB294 \uC11C\uBE44\uC2A4 / \uAE30\uB2A5 \uAD6C\uD604 \uD6C4 <code>Staging</code> \uB2E8\uACC4\uC5D0\uC11C \uC790\uCCB4 \uD14C\uC2A4\uD2B8\uB97C \uAC70\uCE5C\uB2E4. \uC774\uC0C1\uC774 \uC5C6\uB294 \uACBD\uC6B0 <code>Production</code> \uB2E8\uACC4\uB85C Migration \uD558\uC5EC \uC11C\uBE44\uC2A4\uD558\uB294 \uD615\uD0DC --&gt; \uAC01 \uB2E8\uACC4\uB294 <code>\uBC31\uC5C5</code>\uD558\uB294 \uAD6C\uC131\uC77C \uBFD0 \uC544\uB2C8\uB77C \uAC01 \uAE30\uB2A5 \uD639\uC740 \uC11C\uBE44\uC2A4\uC5D0 \uB300\uD55C \uD14C\uC2A4\uD2B8\uB97C \uAC70\uCE58\uACE0 \uC5C5\uB370\uC774\uD2B8 \uD558\uB294 <code>\uBC84\uC804 \uAD00\uB9AC \uAD6C\uC131</code>\uB3C4 \uAC00\uB2A5</p><h2 id="pros-and-cons-of-implementing-3-tier-architecture" tabindex="-1"><a class="header-anchor" href="#pros-and-cons-of-implementing-3-tier-architecture" aria-hidden="true">#</a> Pros and Cons of Implementing 3-Tier Architecture</h2><ul><li><p>\uC7A5\uC810</p><ul><li><p>\uBB3C\uB9AC\uC801 (\uD639\uC740 \uB17C\uB9AC\uC801) \uC11C\uBC84 \uAD6C\uC131\uC744 \uD1B5\uD55C \uB9AC\uC2A4\uD06C \uC644\uD654 --&gt; <code>\uBC31\uC5C5 \uAD6C\uC131</code>\uC774\uB098 <code>\uC774\uC911\uD654</code> \uB4F1\uC758 \uAC00\uB2A5</p></li><li><p>\uC5EC\uB7EC \uB300\uC758 \uC11C\uBC84\uB85C \uB098\uB204\uC5B4 \uACC4\uCE35 \uB3D9\uC791 --&gt; \uC11C\uBC84 \uBD80\uD558 \uAC10\uC18C</p></li><li><p>\uACBD\uC6B0\uC5D0 \uB530\uB978 \uC11C\uBC84 <code>Scale-up</code> \uACE0\uB824 \uAC00\uB2A5</p></li><li><p>\uACC4\uCE35\uBCC4 \uC5C5\uBB34 \uBD84\uB2F4\uC73C\uB85C \uD6A8\uC728\uC131 \uC99D\uAC00</p></li></ul></li><li><p>\uB2E8\uC810</p><ul><li><p>\uAD00\uB9AC\uC758 \uBCF5\uC7A1\uB3C4 \uC0C1\uC2B9</p></li><li><p>\uC7A5\uC560 \uBC1C\uC0DD \uD3EC\uC778\uD2B8 \uC99D\uAC00</p></li><li><p>\uC11C\uBE44\uC2A4 \uADDC\uBAA8\uC640 \uC0AC\uC6A9\uC790 \uADDC\uBAA8\uC5D0 \uB530\uB978 \uBE44\uC6A9 \uACE0\uB824 \uD544\uC694</p></li></ul></li></ul>',32),s=[n];function l(h,u){return i(),a("div",null,s)}var _=e(p,[["render",l],["__file","Multi-Tier-Architecture.html.vue"]]);export{_ as default};
